<span class="filename">eggly.js</span>
<pre class="line-numbers" data-start="49" data-line="4"><code class="language-javascript">function setCurrentCategory(category) {
    $scope.currentCategory = category;

    $state.go('eggly.categories.bookmarks', {category:category.name});

    $scope.cancelCreating();
    $scope.cancelEditing();
